INSERT INTO `email_templates` VALUES (1,'Welcome Template','Welcome to our company','<h1>Welcome!</h1><p>We\'re excited to have you on board.</p>',1,'2025-08-21 18:11:19','2025-08-21 18:11:19'),(8,'sample','sample','sample',1,'2025-08-21 18:39:43','2025-08-21 18:39:43'),(9,'dummy 1','dummy 1','dummy 1',1,'2025-08-22 10:37:33','2025-08-22 10:37:33'),(10,'Welcome Newsletter','Welcome to Our Community!','\n<div style=\"margin: 0; padding: 0; font-family: Arial, sans-serif; background: #f8f9fa;\">\n  <div style=\"max-width: 600px; margin: 0 auto; background: white;\">\n    <div style=\"background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); padding: 40px 20px; text-align: center;\">\n      <p style=\"color: white; font-size: 18px; margin: 10px 0 0 0; opacity: 0.9;\">Thank you for joining our community</p>\n    </div>\n    \n    <div style=\"padding: 40px 20px;\">\n      <p style=\"color: #666; font-size: 16px; line-height: 1.6; margin-bottom: 20px;\">\n        We are excited to have you on board! Our community is a place where you can discover amazing content, \n        connect with like-minded people, and stay updated with the latest trends.\n      </p>\n      \n      <div style=\"text-align: center; margin: 30px 0;\">\n        <a href=\"#\" style=\"display: inline-block; padding: 15px 30px; background: #667eea; color: white; text-decoration: none; border-radius: 5px; font-weight: bold; font-size: 16px;\">\n          Get Started Now\n        </a>\n      </div>\n      \n      <h3 style=\"color: #333; font-size: 20px; margin: 30px 0 15px 0;\">What you can expect:</h3>\n      <ul style=\"color: #666; font-size: 16px; line-height: 1.6; padding-left: 20px;\">\n        <li style=\"margin-bottom: 10px;\">Weekly newsletter with curated content</li>\n        <li style=\"margin-bottom: 10px;\">Exclusive access to premium resources</li>\n        <li style=\"margin-bottom: 10px;\">Community events and networking opportunities</li>\n      </ul>\n    </div>\n    \n    <div style=\"background: #f8f9fa; padding: 30px 20px; text-align: center; border-top: 1px solid #eee;\">\n      <p style=\"color: #999; font-size: 14px; margin: 0;\">\n        You received this email because you signed up for our newsletter.<br>\n        <a href=\"#\" style=\"color: #667eea; text-decoration: none;\">Unsubscribe</a> | \n        <a href=\"#\" style=\"color: #667eea; text-decoration: none;\">Update preferences</a>\n      </p>\n    </div>\n  </div>\n</div>\n',1,'2025-08-25 12:28:25','2025-08-25 12:28:25'),(11,'Product Launch','Exciting New Product Launch!','\n<div style=\"margin: 0; padding: 0; font-family: Arial, sans-serif; background: #f8f9fa;\">\n  <div style=\"max-width: 600px; margin: 0 auto; background: white;\">\n    <div style=\"position: relative; overflow: hidden;\">\n      <img src=\"https://via.placeholder.com/600x300/4facfe/ffffff?text=New+Product\" style=\"width: 100%; height: 300px; object-fit: cover; display: block;\">\n      <div style=\"position: absolute; top: 0; left: 0; right: 0; bottom: 0; background: rgba(79, 172, 254, 0.8); display: flex; align-items: center; justify-content: center;\">\n        <div style=\"text-align: center; color: white;\">\n          <h1 style=\"font-size: 36px; margin: 0; font-weight: bold;\">New Product Launch</h1>\n          <p style=\"font-size: 18px; margin: 10px 0 0 0;\">Innovation meets excellence</p>\n        </div>\n      </div>\n    </div>\n    \n    <div style=\"padding: 40px 20px;\">\n      <h2 style=\"color: #333; font-size: 28px; margin-bottom: 20px; text-align: center;\">Introducing Our Latest Innovation</h2>\n      <p style=\"color: #666; font-size: 16px; line-height: 1.6; margin-bottom: 30px; text-align: center;\">\n        After months of development, we are thrilled to announce the launch of our revolutionary new product \n        that will transform the way you work and live.\n      </p>\n      \n      <div style=\"display: table; width: 100%; table-layout: fixed; margin: 30px 0;\">\n        <div style=\"display: table-cell; width: 50%; padding: 0 10px; vertical-align: top;\">\n          <div style=\"text-align: center; padding: 20px; border: 1px solid #eee; border-radius: 8px; margin-bottom: 20px;\">\n            <h3 style=\"color: #4facfe; font-size: 20px; margin: 0 0 10px 0;\">âš¡ Lightning Fast</h3>\n            <p style=\"color: #666; font-size: 14px; margin: 0;\">Experience unprecedented speed and performance</p>\n          </div>\n        </div>\n        <div style=\"display: table-cell; width: 50%; padding: 0 10px; vertical-align: top;\">\n          <div style=\"text-align: center; padding: 20px; border: 1px solid #eee; border-radius: 8px; margin-bottom: 20px;\">\n            <h3 style=\"color: #4facfe; font-size: 20px; margin: 0 0 10px 0;\">ðŸ”’ Secure</h3>\n            <p style=\"color: #666; font-size: 14px; margin: 0;\">Enterprise-grade security for peace of mind</p>\n          </div>\n        </div>\n      </div>\n      \n      <div style=\"text-align: center; margin: 30px 0;\">\n        <a href=\"#\" style=\"display: inline-block; padding: 15px 40px; background: #4facfe; color: white; text-decoration: none; border-radius: 5px; font-weight: bold; font-size: 18px; margin-right: 10px;\">\n          Learn More\n        </a>\n        <a href=\"#\" style=\"display: inline-block; padding: 15px 40px; background: transparent; color: #4facfe; text-decoration: none; border: 2px solid #4facfe; border-radius: 5px; font-weight: bold; font-size: 18px;\">\n          Watch Demo\n        </a>\n      </div>\n    </div>\n    \n    <div style=\"background: #333; padding: 30px 20px; text-align: center;\">\n      <p style=\"color: white; font-size: 16px; margin: 0 0 10px 0;\">Stay connected with us</p>\n      <div style=\"margin: 20px 0;\">\n        <a href=\"#\" style=\"display: inline-block; margin: 0 10px; color: white; text-decoration: none;\">Facebook</a>\n        <a href=\"#\" style=\"display: inline-block; margin: 0 10px; color: white; text-decoration: none;\">Twitter</a>\n        <a href=\"#\" style=\"display: inline-block; margin: 0 10px; color: white; text-decoration: none;\">LinkedIn</a>\n      </div>\n      <p style=\"color: #999; font-size: 12px; margin: 20px 0 0 0;\">\n        Â© 2025 Your Company. All rights reserved.\n      </p>\n    </div>\n  </div>\n</div>\n',1,'2025-08-25 12:28:25','2025-08-25 12:28:25'),(12,'Monthly Newsletter','Monthly Update - December 2025','\n<div style=\"margin: 0; padding: 0; font-family: Arial, sans-serif; background: #f8f9fa;\">\n  <div style=\"max-width: 600px; margin: 0 auto; background: white;\">\n    <div style=\"background: #333; padding: 20px; text-align: center;\">\n      <h1 style=\"color: white; font-size: 24px; margin: 0;\">Monthly Newsletter</h1>\n      <p style=\"color: #ccc; font-size: 14px; margin: 5px 0 0 0;\">December 2025 Edition</p>\n    </div>\n    \n    <div style=\"padding: 30px 20px;\">\n      <img src=\"https://via.placeholder.com/560x200/43e97b/ffffff?text=Featured+Article\" style=\"width: 100%; height: 200px; object-fit: cover; border-radius: 8px; margin-bottom: 20px;\">\n      \n      <h2 style=\"color: #333; font-size: 24px; margin: 0 0 15px 0;\">Year in Review: Our Biggest Achievements</h2>\n      <p style=\"color: #666; font-size: 16px; line-height: 1.6; margin-bottom: 20px;\">\n        As we wrap up 2025, we want to share some of our proudest moments and biggest milestones \n        from this incredible year. From product launches to community growth, here is what made this year special.\n      </p>\n      \n      <a href=\"#\" style=\"color: #43e97b; text-decoration: none; font-weight: bold;\">Read the full story â†’</a>\n    </div>\n    \n    <hr style=\"border: none; border-top: 1px solid #eee; margin: 0;\">\n    \n    <div style=\"padding: 30px 20px;\">\n      <h3 style=\"color: #333; font-size: 20px; margin: 0 0 20px 0;\">Latest Updates</h3>\n      \n      <div style=\"display: table; width: 100%; margin-bottom: 25px;\">\n        <div style=\"display: table-cell; width: 100px; vertical-align: top;\">\n          <img src=\"https://via.placeholder.com/80x80/fa709a/ffffff?text=News\" style=\"width: 80px; height: 80px; border-radius: 8px;\">\n        </div>\n        <div style=\"display: table-cell; padding-left: 15px; vertical-align: top;\">\n          <h4 style=\"color: #333; font-size: 16px; margin: 0 0 8px 0;\">New Feature Release</h4>\n          <p style=\"color: #666; font-size: 14px; line-height: 1.5; margin: 0;\">\n            We have added powerful new analytics tools to help you track your performance better.\n          </p>\n          <a href=\"#\" style=\"color: #fa709a; text-decoration: none; font-size: 14px;\">Learn more</a>\n        </div>\n      </div>\n      \n      <div style=\"display: table; width: 100%; margin-bottom: 25px;\">\n        <div style=\"display: table-cell; width: 100px; vertical-align: top;\">\n          <img src=\"https://via.placeholder.com/80x80/fee140/ffffff?text=Event\" style=\"width: 80px; height: 80px; border-radius: 8px;\">\n        </div>\n        <div style=\"display: table-cell; padding-left: 15px; vertical-align: top;\">\n          <h4 style=\"color: #333; font-size: 16px; margin: 0 0 8px 0;\">Upcoming Webinar</h4>\n          <p style=\"color: #666; font-size: 14px; line-height: 1.5; margin: 0;\">\n            Join us for an exclusive webinar on industry trends and best practices.\n          </p>\n          <a href=\"#\" style=\"color: #fee140; text-decoration: none; font-size: 14px;\">Register now</a>\n        </div>\n      </div>\n      \n      <div style=\"display: table; width: 100%; margin-bottom: 25px;\">\n        <div style=\"display: table-cell; width: 100px; vertical-align: top;\">\n          <img src=\"https://via.placeholder.com/80x80/66a6ff/ffffff?text=Team\" style=\"width: 80px; height: 80px; border-radius: 8px;\">\n        </div>\n        <div style=\"display: table-cell; padding-left: 15px; vertical-align: top;\">\n          <h4 style=\"color: #333; font-size: 16px; margin: 0 0 8px 0;\">Team Expansion</h4>\n          <p style=\"color: #666; font-size: 14px; line-height: 1.5; margin: 0;\">\n            We are growing! Meet our new team members and learn about their expertise.\n          </p>\n          <a href=\"#\" style=\"color: #66a6ff; text-decoration: none; font-size: 14px;\">Meet the team</a>\n        </div>\n      </div>\n    </div>\n    \n    <hr style=\"border: none; border-top: 1px solid #eee; margin: 0;\">\n    \n    <div style=\"background: linear-gradient(135deg, #89f7fe 0%, #66a6ff 100%); padding: 30px 20px; text-align: center;\">\n      <h3 style=\"color: white; font-size: 22px; margin: 0 0 15px 0;\">Ready to Get Started?</h3>\n      <p style=\"color: white; font-size: 16px; margin: 0 0 20px 0; opacity: 0.9;\">\n        Join thousands of satisfied customers who trust our platform\n      </p>\n      <a href=\"#\" style=\"display: inline-block; padding: 12px 30px; background: white; color: #66a6ff; text-decoration: none; border-radius: 25px; font-weight: bold;\">\n        Start Free Trial\n      </a>\n    </div>\n    \n    <div style=\"background: #f8f9fa; padding: 20px; text-align: center; font-size: 12px; color: #999;\">\n      <p style=\"margin: 0 0 10px 0;\">\n        You are receiving this because you subscribed to our newsletter.\n      </p>\n      <p style=\"margin: 0;\">\n        <a href=\"#\" style=\"color: #666; text-decoration: none;\">Unsubscribe</a> | \n        <a href=\"#\" style=\"color: #666; text-decoration: none;\">Manage Preferences</a> | \n        <a href=\"#\" style=\"color: #666; text-decoration: none;\">Forward to a Friend</a>\n      </p>\n    </div>\n  </div>\n</div>\n',1,'2025-08-25 12:28:25','2025-08-25 12:28:25');
